function Input(props) {
  const {setSelectedValue,type,selectedValue,onChange,value} = props.value || props
  const { styles } = defaultStyles;

  return (
    <div>
      <Style.Input
        style={styles.textInput.extraSmallTextInput}
        type={type ? type : "text"}
        value={value}
